let container=document.getElementById("word-container"),allWords=[];fetch("words.json").then(e=>e.json()).then(e=>{allWords=e,populateContainer(allWords,container),console.log(allWords)}).catch(e=>console.log("There was an error:",e));function populateContainer(e,t){e&&Array.isArray(e)?e.forEach(e=>{let s=Math.floor(Math.random()*100)+10,o=Math.floor(Math.random()*100)+10,n=document.createElement("div");n.className="word",n.style.position="relative",n.style.paddingLeft=s+"px",n.style.paddingTop=o+"px",n.style.fontSize=`${e.fontSize}`+"px",n.innerText=`${e.word}`,t.appendChild(n)}):console.error("Data is undefined or not an array.")}container.addEventListener("scroll",()=>{const{scrollTop:e,scrollHeight:t,clientHeight:n}=container;e+n>=t-5&&populateContainer(allWords,container)})